mapper.update:
update daily_train_ticket set

  ydz = ydz - 1


  edz = edz - 1


  rw = rw - 1


  yw = yw - 1

where `date` = #{date}
and train_code = #{trainCode}
and start_index >= #{minStartIndex}
and start_index <= #{maxStartIndex}
and end_index >= #{minEndIndex}
and end_index <= #{maxEndIndex}
